import React, { useContext, useReducer } from 'react';
import BeerContext from '../context/beers';

function init(beers) {
  return beers.reduce(
    (acc, beer) => ({
      ...acc,
      [beer.id]: 0,
    }),
    {}
  );
}

function reducer(state, action) {
  switch (action.type) {
    case 'increment':
      return { ...state, [action.payload]: state[action.payload] + 1 };
    case 'decrement':
      return { ...state, [action.payload]: state[action.payload] - 1 };
    default:
      throw new Error();
  }
}

